create database dndsheet character set utf8;
use dndsheet;
create table race (id int not null auto_increment primary key, name varchar(20), description longtext, max_age int, language varchar(20), size varchar(20), speed int, feats longtext);
create table class (id int not null auto_increment primary key, name varchar(20), description longtext, level int, level_hp int, armor varchar (50), weapons varchar(50), abilities longtext);
create table playerCharacter (id int not null auto_increment primary key, name varchar(50), alias varchar(50), age int, race_id int, deity varchar(50), str int, dex int, con int, inte int, wis int, cha int, foreign key (race_id) references race(id));
create table playerCharacter_class (id int not null auto_increment primary key, playercharacter_id int, class_id int, level int not null, foreign key (playercharacter_id) references playerCharacter(id), foreign key (class_id) references class(id));
